(130, 'DirectoryWizard_Options', 'a:24:{s:14:"wdw_front_page";i:4;s:26:"wdw_global_setting_page_id";i:0;s:11:"category_id";i:0;s:18:"wdw_search_keyword";s:0:"";s:17:"wdw_taxonomy_slug";s:8:"category";s:18:"wdw_post_type_slug";s:3:"tag";s:19:"wdw_email_from_name";s:4:"buin";s:20:"wdw_email_from_email";s:13:"test@test.com";s:28:"wdw_email_notification_email";s:13:"test@test.com";s:25:"wdw_email_toadmin_subject";s:44:"A new listing has been submitted for review!";s:22:"wdw_email_toadmin_body";s:341:"A new listing is pending review!\n\nThis submission is awaiting approval. Please visit the link to view and approve the new listing:\n\n{approve_link}\n\nListing Name: {title}\nListing Description: {description}\n\n*****************************************\nThis is an automated message from {site_title}\nPlease do not respond directly to this email\n\n";s:26:"wdw_email_toauthor_subject";s:39:"Your listing on buin is pending review!";s:23:"wdw_email_toauthor_body";s:456:"Thank you for submitting a listing to {site_title}!\n\nYour listing is pending approval.\n\nPlease review the following information for accuracy, as this is what will appear on our web site. If you see any errors, please contact us immediately at {directory_email}.\n\nListing Name: {title}\nListing Description: {description}\n\n*****************************************\nThis is an automated message from {site_title}\nPlease do not respond directly to this email\n\n";s:24:"wdw_email_public_subject";s:39:"Your listing on buin has been approved!";s:21:"wdw_email_public_body";s:257:"Thank you for submitting a listing to {site_title}!\n\nYour listing has been approved! You can now view it online:\n\n{link}\n\n*****************************************\nThis is an automated message from {site_title}\nPlease do not respond directly to this email\n\n";s:27:"wdw_submit_terms_of_service";s:8:"Welcome!";s:22:"wdw_submit_top_message";s:198:"<p>Please tell us a little bit about the organization you would like to see listed in our directory. Try to include as much information as you can, and be as descriptive as possible where asked.</p>";s:26:"wdw_submit_success_message";s:194:"<h3>Congratulations!</h3><p>Your listing has been successfully submitted for review. Please allow us sufficient time to review the listing and approve it for public display in our directory.</p>";s:22:"wdw_submit_require_tos";s:1:"0";s:18:"wdw_use_google_map";s:1:"1";s:19:"wdw_directory_label";s:8:"Welcome!";s:25:"wdw_directory_description";s:0:"";s:18:"wdw_author_linking";s:1:"1";s:14:"wdw_initial_dt";i:1418075927;}', 'yes'),
